        /// <summary>
        /// Configures the HttpClients.
        /// </summary>
        /// <param name="client"></param>
        /// <param name="builder"></param>
        /// <returns></returns>
        public HostConfiguration AddApiHttpClients
        (
            Action<HttpClient>? client = null, Action<IHttpClientBuilder>? builder = null)
        {
            if (client == null)
                client = c => c.BaseAddress = new Uri(ClientUtils.BASE_ADDRESS);

            List<IHttpClientBuilder> builders = new List<IHttpClientBuilder>();

            builders.Add(_services.AddHttpClient<IArtifactoryApi, ArtifactoryApi>(client));
            builders.Add(_services.AddHttpClient<IAssetsApi, AssetsApi>(client));
            builders.Add(_services.AddHttpClient<IBuildApi, BuildApi>(client));
            builders.Add(_services.AddHttpClient<ICollaboratorsApi, CollaboratorsApi>(client));
            builders.Add(_services.AddHttpClient<IContributorsApi, ContributorsApi>(client));
            builders.Add(_services.AddHttpClient<IFollowersApi, FollowersApi>(client));
            builders.Add(_services.AddHttpClient<IGitApi, GitApi>(client));
            builders.Add(_services.AddHttpClient<IGitSettingsApi, GitSettingsApi>(client));
            builders.Add(_services.AddHttpClient<IIssuesApi, IssuesApi>(client));
            builders.Add(_services.AddHttpClient<IMissionsApi, MissionsApi>(client));
            builders.Add(_services.AddHttpClient<IOrganizationsApi, OrganizationsApi>(client));
            builders.Add(_services.AddHttpClient<IPullsApi, PullsApi>(client));
            builders.Add(_services.AddHttpClient<IReleasesApi, ReleasesApi>(client));
            builders.Add(_services.AddHttpClient<IRepoContributorApi, RepoContributorApi>(client));
            builders.Add(_services.AddHttpClient<IRepoLabelsApi, RepoLabelsApi>(client));
            builders.Add(_services.AddHttpClient<IRepositoriesApi, RepositoriesApi>(client));
            builders.Add(_services.AddHttpClient<IStarringApi, StarringApi>(client));
            builders.Add(_services.AddHttpClient<IUsersApi, UsersApi>(client));
            builders.Add(_services.AddHttpClient<IWorkspaceApi, WorkspaceApi>(client));
            
            if (builder != null)
                foreach (IHttpClientBuilder instance in builders)
                    builder(instance);

            HttpClientsAdded = true;

            return this;
        }

        /// <summary>
        /// Configures the JsonSerializerSettings
        /// </summary>
        /// <param name="options"></param>
        /// <returns></returns>
        public HostConfiguration ConfigureJsonOptions(Action<JsonSerializerOptions> options)
        {
            options(_jsonOptions);

            return this;
        }

        /// <summary>
        /// Adds tokens to your IServiceCollection
        /// </summary>
        /// <typeparam name="TTokenBase"></typeparam>
        /// <param name="token"></param>
        /// <returns></returns>
        public HostConfiguration AddTokens<TTokenBase>(TTokenBase token) where TTokenBase : TokenBase
        {
            return AddTokens(new TTokenBase[]{ token });
        }

        /// <summary>
        /// Adds tokens to your IServiceCollection
        /// </summary>
        /// <typeparam name="TTokenBase"></typeparam>
        /// <param name="tokens"></param>
        /// <returns></returns>
        public HostConfiguration AddTokens<TTokenBase>(IEnumerable<TTokenBase> tokens) where TTokenBase : TokenBase
        {
            TokenContainer<TTokenBase> container = new TokenContainer<TTokenBase>(tokens);
            _services.AddSingleton(services => container);

            return this;
        }

        /// <summary>
        /// Adds a token provider to your IServiceCollection
        /// </summary>
        /// <typeparam name="TTokenProvider"></typeparam>
        /// <typeparam name="TTokenBase"></typeparam>
        /// <returns></returns>
        public HostConfiguration UseProvider<TTokenProvider, TTokenBase>() 
            where TTokenProvider : TokenProvider<TTokenBase>
            where TTokenBase : TokenBase
        {
            _services.AddSingleton<TTokenProvider>();
            _services.AddSingleton<TokenProvider<TTokenBase>>(services => services.GetRequiredService<TTokenProvider>());

            return this;
        }
    }
