                writer.WriteBoolean("required_must_push_via_pull_request", webPullRequestBranchProtection.RequiredMustPushViaPullRequestOption.Value!.Value);

            if (webPullRequestBranchProtection.RequiredPullRequestReviewsOption.IsSet)
                writer.WriteBoolean("required_pull_request_reviews", webPullRequestBranchProtection.RequiredPullRequestReviewsOption.Value!.Value);

            if (webPullRequestBranchProtection.RequiredStatusChecksOption.IsSet)
                writer.WriteBoolean("required_status_checks", webPullRequestBranchProtection.RequiredStatusChecksOption.Value!.Value);

            if (webPullRequestBranchProtection.RuleOption.IsSet)
                writer.WriteString("rule", webPullRequestBranchProtection.Rule);
        }
    }
}
